    <span class="hljs-keyword">public</span> <span class="hljs-keyword">class</span> <span class="hljs-title">VigenereEncoder</span> : <span class="hljs-title">IEncoder</span>&lt;<span class="hljs-title">string</span>&gt;
    {
        <span class="hljs-keyword">private</span> <span class="hljs-keyword">readonly</span> CaesarEncoder caesarEncoder = <span class="hljs-keyword">new</span>();

