                <span class="hljs-keyword">var</span> encodedSymbol = symbolCipher(text[i].ToString(), letterZ - key[i]);
                _ = encodedTextBuilder.Append(encodedSymbol);
            }

            <span class="hljs-keyword">return</span> encodedTextBuilder.ToString();
        }

        <span class="hljs-function"><span class="hljs-keyword">private</span> <span class="hljs-built_in">string</span> <span class="hljs-title">AppendKey</span>(<span class="hljs-params"><span class="hljs-built_in">string</span> key, <span class="hljs-built_in">int</span> length</span>)</span>
